import React,{Component} from 'react'
import Footer from '../common/footer'
import {connect} from 'react-redux';
import actions from './actionCreator'
class Car extends Component {
	render(){
			console.log(this.props)
			return <div><ul>
					   {
							 this.props.list.map((item)=>{
								   return <li key={item.id}>
											   <input type="checkbox" defaultChecked={item.flag} onChange={this.props.change.bind(this,item.id)} />
												   <img src={item.imgUrl} width="100" height="100" />
												   <div>

														   <h3>{item.name}</h3>
														   <p>{item.desc}</p>
														   <p>{item.price}</p>
														   <button onClick={this.props.inc.bind(this,item.id)}>+</button>
														   {item.count}
														   <button onClick={this.props.dec.bind(this,item.id)}>-</button>
												   </div>

											</li>
							 })
					   }
				   </ul>
					   {this.props.count()}   {this.props.money()}
					   <Footer />
				   </div>

		   
	   }
}

var mapState=(state)=>{
	return {
		list:state.car.car,
		count(){
			var s=0;
			state.car.car.forEach((item)=>{
				if(item.flag){
					s+=item.count;	
				}
				
			})
			return s;
		},
	}
}
var mapDispatch=(dispatch)=>{
return {
	inc(id){
		
		dispatch(actions.incAction(id));
	},
	dec(id){
		dispatch(actions.decAction(id));
	},
	change(id){
		dispatch(actions.changeAction(id));
	}
}
}
export default connect(mapState,mapDispatch)(Car);
